export class Grad {
    constructor(x, y, z, w) {
        this.x = x;
        this.y = y;
        this.z = z;
        this.w = w;
    }
    dot2(x, y) {
        return this.x * x + this.y * y;
    }
    dot3(x, y, z) {
        return this.dot2(x, y) + this.z * z;
    }
    dot4(x, y, z, w) {
        return this.dot3(x, y, z) + this.w * w;
    }
}
